Cards::Cards(const Cards& r) {
    this->Ability = r.Ability;
    this->Ability_Type = r.Ability_Type;
}

const Cards& Cards::operator=(const Cards& r) {
    if (this != &r) {
        Ability = r.Ability;
        Ability_Type = r.Ability_Type;
    }
    return *this;
}

Cards::~Cards() {
}

bool Cards::Get_Ability() {
    return this->Ability;
}

int Cards::Get_Ability_Type() {
    return this->Ability_Type;
}

Cards Create_Card() {
    Cards C;
    return C;
}