Organism::Organism(int H, int P, int D, int S_, int D_) {
	health = (H * S_) / 10;
	protection = (P * S_) / 10;
	damage = (D * D_) / 10;
	security_settings = S_;
	damage_settings = D_;
}

Organism::Organism(const Organism& r) {
	health = r.health;
	protection = r.protection;
	damage = r.damage;
	security_settings = r.security_settings;
	damage_settings = r.damage_settings;
}

const Organism& Organism::operator=(const Organism& r) {
	if (this != &r) {
		health = r.health;
		protection = r.protection;
		damage = r.damage;
		security_settings = r.security_settings;
		damage_settings = r.damage_settings;
	}
	return *this;
}

