Mob Create_Mob() {
	int H, P, D, S_, D_;

	H = rand() % 400 + 100;
	P = rand() % 80 + 5;
	D = rand() % 90 + 10;
	S_ = rand() % 12 + 7;
	D_ = rand() % 12 + 7;
	Mob M(H, P, D, S_, D_);
	return M;
}

int Count(int x) {
	int Counter = 0;
	while (x != 0) {
		x /= 10;
		Counter++;
	}
	return Counter;
}

Mob mob;
int Mob_Counter = 0;
bool Hero::Figt_with_Mob() {
	std::vector<std::vector<int>> Mob_;
	switch (Mob_Counter) {
	case 0: {
		Mob_ = Mob_1;
		break;
	}
	case 1: {
		Mob_ = Mob_2;
		break;
	}
	case 2: {
		Mob_ = Mob_3;
		break;
	}
	case 3: {
		Mob_ = Mob_4;
		break;
	}
	case 4: {
		Mob_ = Mob_5;
		break;
	}
	case 5: {
		Mob_ = Mob_6;
		break;
	}
	case 6: {
		Mob_ = Mob_7;
		break;
	}
	case 7: {
		Mob_ = Mob_8;
		break;
	}
	default:
		break;
	}
	mob = Create_Mob();
	int Exit = 1;
	Mob_Counter++;
	int Temp_health = this->GetHealth();
	int Temp_protection = this->GetProtection();
	int Temp_damage = this->GetDamage();
	while ((this->GetHealth() > 0) and (mob.GetHealth() > 0) and Exit < 5) {
		Exit++; 
		Clear_Consol();
		std::cout << Drow_Fight(Mob_, Void_Visual, Hero_Visual);
		Drow_Earth();
		std::cout << "Mob health: " << mob.GetHealth() << std::setw(105 - Count(mob.GetHealth()));
		std::cout << "Hero health: " << this->GetHealth() << std::endl;
		std::cout << "Mob protection: " << mob.GetProtection() << std::setw(105 - Count(mob.GetProtection()));
		std::cout << "Hero protection: " << this->GetProtection() << std::endl;
		std::cout << "Mob damage: " << mob.GetDamage() << std::setw(105 - Count(mob.GetDamage()));
		std::cout << "Hero damage: " << this->GetDamage() << std::endl;

		Sleep(900);
		Clear_Consol();
		system("color 04");


		if (Exit % 2 == 1) {// 
			std::cout << Drow_Fight(Mob_, Attak_Visual, Hero_Visual);

			if (this->GetFlag_H()) this->AddHealth(50);
			if (this->GetFlag_P()) this->AddProtection(5);
			if (this->GetFlag_D())this->AddDamage(5);

			if (this->GetDamage() > mob.GetProtection()) {
				mob.AddHealth((-1) * this->GetDamage());
			}
			if (mob.GetHealth() <= 0) {
				this->AssignHealth(Temp_health);
				this->AssignProtection(Temp_protection);
				this->AssignDamage(Temp_damage);
				return 1;
			}

		}
		else {// 
			std::cout << Drow_Fight(Void_Visual, Mob_, Hero_Visual);
			if (mob.GetDamage() > this->GetProtection()) {
				this->AddHealth((-1) * mob.GetDamage());
			}
			if (this->GetHealth() <= 0) return -1;
		}
		Drow_Earth();


		Sleep(900);
		system("color 07");
		Clear_Consol();

		std::cout << Drow_Fight(Mob_, Void_Visual, Hero_Visual);
		Drow_Earth();
		std::cout << "Mob health: " << mob.GetHealth() << std::setw(105 - Count(mob.GetHealth()));
		std::cout << "Hero health: " << this->GetHealth() << std::endl;
		std::cout << "Mob protection: " << mob.GetProtection() << std::setw(105 - Count(mob.GetProtection()));
		std::cout << "Hero protection: " << this->GetProtection() << std::endl;
		std::cout << "Mob damage: " << mob.GetDamage() << std::setw(105 - Count(mob.GetDamage()));
		std::cout << "Hero damage: " << this->GetDamage() << std::endl;

		Sleep(900);
	}
	this->AssignHealth(Temp_health);
	this->AssignProtection(Temp_protection);
	this->AssignDamage(Temp_damage);
	return 1;
}

