int Hero::Play(std::vector<std::vector<int>>& Map) {
	int Temp = 0;
	
	char symbol = _getch();
	symbol = _getch();

		switch (symbol) {
		case 66:
			if (Map[this->Get_Y()][this->Get_X()] == 66) {
				bool flag = this->Fight_with_Boss();
				Clear_Consol();
				system("color 07");
				if (flag) return 2;
				else return -1;
			}
			break;
		case 69:
			return 0;
			break;
		case 70:
			if (Map[this->Get_Y()][this->Get_X()] == 70) {
				bool flag = this->Figt_with_Mob();
				system("color 07");
				if (flag) {
					Map[this->Get_Y()][this->Get_X()] = 88;
					return 3;
				}
				else return -1;
			}
			break;
		case 72:
			if (Map[this->Get_Y() - 1][this->Get_X()] != 219) {
				this->Y--;
				Temp = Map[this->Get_Y()][this->Get_X()];
				Map[this->Get_Y()][this->Get_X()] = 1;
				Clear_Consol();
				std::cout << Labyrinth_drawing(Map);
				Map[this->Get_Y()][this->Get_X()] = Temp;
			}
			break;
		case 75:
			if (Map[this->Get_Y()][this->Get_X() - 1] != 219) {
				this->X--;
				Temp = Map[this->Get_Y()][this->Get_X()];
				Map[this->Get_Y()][this->Get_X()] = 1;
				Clear_Consol();
				std::cout << Labyrinth_drawing(Map);
				Map[this->Get_Y()][this->Get_X()] = Temp;
			}
			break;
		case 77:
			if (Map[this->Get_Y()][this->Get_X() + 1] != 219) {
				this->X++;
				Temp = Map[this->Get_Y()][this->Get_X()];
				Map[this->Get_Y()][this->Get_X()] = 1;
				Clear_Consol();
				std::cout << Labyrinth_drawing(Map);
				Map[this->Get_Y()][this->Get_X()] = Temp;
			}
			break;
		case 80:
			if (Map[this->Get_Y() + 1][this->Get_X()] != 219) {
				this->Y++;
				Temp = Map[this->Get_Y()][this->Get_X()];
				Map[this->Get_Y()][this->Get_X()] = 1;
				Clear_Consol();
				std::cout << Labyrinth_drawing(Map);
				Map[this->Get_Y()][this->Get_X()] = Temp;
			}
			break;
		default:
			return 1;
			break;
		}

	return 1;
}

