std::string Labyrinth_drawing(std::vector<std::vector<int>>& Labyrinth) {
	std::stringstream ss;
	for (size_t i = 0; i < Labyrinth.size(); ++i) {
		for (size_t j = 0; j < Labyrinth[i].size(); ++j) {
			ss << char(Labyrinth[i][j]);
		}
		ss << std::endl;
	}
	return ss.str();
}

std::string Drow_Fight(std::vector<std::vector<int>>& vec1,
	                   std::vector<std::vector<int>>& vec2,
	                   std::vector<std::vector<int>>& vec3) {
	std::stringstream ss;
	ss << std::endl << std::endl << std::endl;
	for (size_t i = 0; i < vec1.size(); ++i) {
		for (size_t j = 0; j < vec1[i].size(); ++j) {
			ss << char(vec1[i][j]);
		}
		for (size_t j = 0; j < vec2[i].size(); ++j) {
			ss << char(vec2[i][j]);
		}
		for (size_t j = 0; j < vec3[i].size(); ++j) {
			ss << char(vec3[i][j]);
		}
		ss << std::endl;
	}
	return ss.str();
}

